import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as api from '../utils/api';


export default function Login({ onLogin }) {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        // Clear error when user starts typing
        if (errors[e.target.name]) {
            setErrors({
                ...errors,
                [e.target.name]: ''
            });
        }
    };

    const validateForm = () => {
        const newErrors = {};
        
        if (!formData.username.trim()) {
            newErrors.username = 'Username is required';
        }
        
        if (!formData.password.trim()) {
            newErrors.password = 'Password is required';
        }
        
        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const validationErrors = validateForm();
        
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        setLoading(true);
        setErrors({});

        try {
            const response = await api.login(formData);
            const { user, token } = response.data;
            
            // Store user and token in localStorage
            localStorage.setItem('user', JSON.stringify(user));
            localStorage.setItem('authToken', token);
            
            // Call parent component's onLogin if provided
            if (onLogin) {
                onLogin(user);
            }

            // Redirect based on role
            if (user.role === 'TEACHER') {
                navigate('/teacher/dashboard');
            } else {
                navigate('/student/dashboard');
            }
        } catch (error) {
            setErrors({
                general: error.response?.data?.message || 'Login failed. Please try again.'
            });
        } finally {
            setLoading(false);
        }
    };

    const goToSignup = () => {
        navigate('/signup');
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2 className="auth-title">Login to Exam System</h2>
                
                {errors.general && (
                    <div className="error-message">{errors.general}</div>
                )}
                
                <form onSubmit={handleSubmit} className="auth-form">
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            className={errors.username ? 'error' : ''}
                            placeholder="Enter your username"
                        />
                        {errors.username && (
                            <span className="field-error">{errors.username}</span>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            className={errors.password ? 'error' : ''}
                            placeholder="Enter your password"
                        />
                        {errors.password && (
                            <span className="field-error">{errors.password}</span>
                        )}
                    </div>

                    <button 
                        type="submit" 
                        className="auth-button"
                        disabled={loading}
                    >
                        {loading ? 'Logging in...' : 'Login'}
                    </button>
                </form>

                <div className="auth-footer">
                    <div style={{ marginBottom: '20px', padding: '20px', background: '#e8f5e8', borderRadius: '8px', border: '2px solid #28a745' }}>
                        <h3 style={{ margin: '0 0 15px 0', color: '#155724' }}>‚úÖ Quick Login (Bypass API)</h3>
                        <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
                            <button 
                                type="button"
                                onClick={() => {
                                    const teacherUser = { username: 'teacher1', role: 'TEACHER', fullName: 'Test Teacher' };
                                    localStorage.setItem('user', JSON.stringify(teacherUser));
                                    if (onLogin) onLogin(teacherUser);
                                    navigate('/test-dashboard');
                                }}
                                style={{
                                    padding: '12px 24px',
                                    background: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '6px',
                                    cursor: 'pointer',
                                    fontSize: '16px',
                                    fontWeight: 'bold'
                                }}
                            >
                                üë®‚Äçüè´ Login as Teacher
                            </button>
                            <button 
                                type="button"
                                onClick={() => {
                                    const studentUser = { username: 'student1', role: 'STUDENT', fullName: 'Test Student' };
                                    localStorage.setItem('user', JSON.stringify(studentUser));
                                    if (onLogin) onLogin(studentUser);
                                    navigate('/test-exams');
                                }}
                                style={{
                                    padding: '12px 24px',
                                    background: '#28a745',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '6px',
                                    cursor: 'pointer',
                                    fontSize: '16px',
                                    fontWeight: 'bold'
                                }}
                            >
                                üéì Login as Student
                            </button>
                        </div>
                    </div>
                    
                    <p>
                        Don't have an account? 
                        <button 
                            type="button" 
                            className="link-button" 
                            onClick={goToSignup}
                        >
                            Sign up here
                        </button>
                    </p>
                </div>
            </div>
        </div>
    );
}
