import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, useParams } from 'react-router-dom';
import Navbar from './components/Navbar';
import ExamCreator from './components/ExamCreator';
import StudentExamList from './components/StudentExamList';
import ExamInterface from './components/ExamInterface';
import ExamResults from './components/ExamResults';
import TeacherDashboard from './components/TeacherDashboard';
import Login from './components/Login';
import Signup from './components/Signup';
import TestConnection from './components/TestConnection';

// Import all styles
import './styles/index.css';
import './styles/navigation.css';
import './styles/auth.css';
import './styles/dashboard.css';
import './styles/forms.css';
import './styles/lists.css';
import './styles/exam.css';

function ProtectedRoute({ children, requiredRole }) {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            const parsedUser = JSON.parse(userData);
            if (!requiredRole || parsedUser.role === requiredRole) {
                setUser(parsedUser);
            }
        }
        setLoading(false);
    }, [requiredRole]);

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (!user) {
        return <Login />;
    }

    return children;
}

function ExamInterfaceWrapper() {
    const location = useLocation();
    const params = useParams();
    
    if (!location.state) {
        return (
            <ExamInterface
                location={{
                    state: {
                        questions: [
                            {
                                questionId: 11,
                                questionText: 'Fallback Q1?',
                                optionA: 'A',
                                optionB: 'B',
                                optionC: 'C',
                                optionD: 'D',
                                marks: 2,
                            }
                        ],
                        exam: { duration: 15 },
                        studentExamId: params.studentExamId,
                        studentUsername: 'student1',
                    }
                }}
            />
        );
    }
    return <ExamInterface location={location} />;
}

function App() {
    const [user, setUser] = useState(null);

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            try {
                setUser(JSON.parse(userData));
            } catch (error) {
                console.error('Error parsing user data:', error);
                localStorage.removeItem('user');
            }
        }
    }, []);

    const handleLogin = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    return (
        <Router>
            <Navbar />
            
            <Routes>
                <Route path="/" element={
                    <div className="dashboard-container">
                        <div className="container">
                            <div className="welcome-section">
                                <div className="welcome-content">
                                    <h1 className="welcome-title">Online Exam System</h1>
                                    {user ? (
                                        <div>
                                            <p className="welcome-subtitle">Welcome back, {user.fullName || user.name || user.username}!</p>
                                            <p>Role: {user.role}</p>
                                            <div className="welcome-actions">
                                                {user.role === 'TEACHER' ? (
                                                    <>
                                                        <a href="/teacher/dashboard" className="welcome-btn">Go to Dashboard</a>
                                                        <a href="/create-exam" className="welcome-btn">Create New Exam</a>
                                                    </>
                                                ) : (
                                                    <>
                                                        <a href="/student/dashboard" className="welcome-btn">Go to Dashboard</a>
                                                        <a href="/student/exams" className="welcome-btn">View Exams</a>
                                                    </>
                                                )}
                                            </div>
                                        </div>
                                    ) : (
                                        <div>
                                            <p className="welcome-subtitle">Please login to access the exam system.</p>
                                            <div className="welcome-actions">
                                                <a href="/login" className="welcome-btn">Login</a>
                                                <a href="/signup" className="welcome-btn">Sign Up</a>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                } />
                
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/signup" element={<Signup />} />
                
                {/* Teacher Routes */}
                <Route path="/teacher/dashboard" element={
                    <ProtectedRoute requiredRole="TEACHER">
                        <TeacherDashboard teacherUsername={user?.username || 'teacher1'} />
                    </ProtectedRoute>
                } />
                
                <Route path="/create-exam" element={
                    <ProtectedRoute requiredRole="TEACHER">
                        <ExamCreator />
                    </ProtectedRoute>
                } />

                <Route path="/edit-exam/:examId" element={
                    <ProtectedRoute requiredRole="TEACHER">
                        <ExamCreator />
                    </ProtectedRoute>
                } />
                
                {/* Student Routes */}
                <Route path="/student/dashboard" element={
                    <ProtectedRoute requiredRole="STUDENT">
                        <div className="dashboard-container">
                            <div className="container">
                                <div className="welcome-section">
                                    <div className="welcome-content">
                                        <h1 className="welcome-title">Student Dashboard</h1>
                                        <p className="welcome-subtitle">Welcome, {user?.fullName || user?.name || user?.username}!</p>
                                        <div className="welcome-actions">
                                            <a href="/student/exams" className="welcome-btn">View Available Exams</a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </ProtectedRoute>
                } />
                
                <Route path="/student/exams" element={
                    <ProtectedRoute requiredRole="STUDENT">
                        <StudentExamList />
                    </ProtectedRoute>
                } />
                
                <Route path="/student/exams/:studentExamId" element={
                    <ProtectedRoute requiredRole="STUDENT">
                        <ExamInterfaceWrapper />
                    </ProtectedRoute>
                } />
                
                <Route path="/student/results/:studentExamId" element={
                    <ProtectedRoute requiredRole="STUDENT">
                        <ExamResults />
                    </ProtectedRoute>
                } />

                {/* About and Contact pages */}
                <Route path="/about" element={
                    <div className="dashboard-container">
                        <div className="container">

<h1>About Us</h1><br/>

<h3>🎯 Our Mission</h3>
<p>
  We aim to revolutionize exams with a <strong>digital, reliable, and accessible</strong> platform that saves time, reduces effort, and ensures fairness.
</p>

<h3>🌟 Core Values</h3>
<ul>
  <li>🤝 <strong>Integrity:</strong> Fair and transparent exams</li>
  <li>🌍 <strong>Accessibility:</strong> Anytime, anywhere</li>
  <li>💡 <strong>Innovation:</strong> Smarter learning with technology</li>
  <li>🛡 <strong>Reliability:</strong> Secure and stress-free</li>
</ul><br/>

<h3>⚡ Key Features</h3>
<ul>
  <li>👩‍🏫 Teacher dashboard to create & manage exams</li>
  <li>🎓 Student-friendly interface with easy navigation</li>
  <li>⏱ Instant grading & detailed reports</li>
  <li>🔐 Secure login with role-based access</li>
</ul><br/>

<h3>💡 Why Choose Us?</h3>
<p>
  We bridge the gap between <strong>traditional exams</strong> and <strong>modern digital solutions</strong>, making assessments faster, smarter, and stress-free for everyone.
</p>
                            </div>
                    </div>
                } />

                <Route path="/contact" element={
                    <div className="dashboard-container">
                        <div className="container">

                                <h1>Contact Us</h1>
<p>We’d love to hear from you! Reach out with any questions, feedback, or support needs.</p>

<h3>📧 Email</h3>
<p>support@examsystem.com</p>

<h3>📞 Phone</h3>
<p>+91-1234567890</p>

<h3>🏢 Office Address</h3>
<p>44/1 Bharat Apartment, 4C<br/>5th Main Road, Jayanagar<br/>
Bangalore - 560041<br/>
Karnataka, India </p>

<h3>⏰ Working Hours</h3>
<p>Monday – Friday: 9:00 AM – 6:00 PM (EST)</p>

<h3>🌐 Connect With Us</h3>
<ul>
  <li><a href="#">Facebook</a></li>
  <li><a href="#">Twitter</a></li>
  <li><a href="#">LinkedIn</a></li>
</ul>


                        </div>
                    </div>
                } />
                <Route path="/test" element={<TestConnection />} />
                <Route path="/test-dashboard" element={<TeacherDashboard teacherUsername="teacher1" />} />
            </Routes>
        </Router>
    );
}

export default App;
