import React, { useState, useEffect } from 'react';
import * as api from '../utils/api';

export default function TestConnection() {
    const [status, setStatus] = useState('Testing...');
    const [results, setResults] = useState({});

    useEffect(() => {
        testConnection();
    }, []);

    const testConnection = async () => {
        try {
            // Test basic connection
            const healthResponse = await fetch('http://localhost:9090/api/test/health');
            const healthData = await healthResponse.json();
            
            // Test database connection
            const dbResponse = await fetch('http://localhost:9090/api/test/db');
            const dbData = await dbResponse.json();
            
            setResults({
                backend: healthData,
                database: dbData
            });
            setStatus('Connected');
        } catch (error) {
            setStatus('Failed: ' + error.message);
        }
    };

    const testAuth = async () => {
        try {
            const response = await api.login({
                username: 'test',
                password: 'test123'
            });
            alert('Login test: ' + JSON.stringify(response.data));
        } catch (error) {
            alert('Login test failed: ' + error.message);
        }
    };

    const testSimpleExam = async () => {
        try {
            const response = await api.createSimpleExam({
                title: 'Test Exam',
                description: 'Test Description',
                duration: 60
            });
            alert('Simple exam test: ' + JSON.stringify(response.data));
        } catch (error) {
            alert('Simple exam test failed: ' + error.message);
        }
    };

    const testSimpleExams = async () => {
        try {
            const response = await api.getSimpleExams();
            alert('Get simple exams test: ' + JSON.stringify(response.data));
        } catch (error) {
            alert('Get simple exams test failed: ' + error.message);
        }
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'monospace' }}>
            <h2>Connection Test</h2>
            <p>Status: {status}</p>
            
            <h3>Backend Health:</h3>
            <pre>{JSON.stringify(results.backend, null, 2)}</pre>
            
            <h3>Database Status:</h3>
            <pre>{JSON.stringify(results.database, null, 2)}</pre>
            
            <button onClick={testAuth} style={{ margin: '10px', padding: '10px' }}>
                Test Login API
            </button>
            
            <button onClick={testConnection} style={{ margin: '10px', padding: '10px' }}>
                Refresh Test
            </button>
            
            <button onClick={testSimpleExam} style={{ margin: '10px', padding: '10px' }}>
                Test Simple Exam Creation
            </button>
            
            <button onClick={testSimpleExams} style={{ margin: '10px', padding: '10px' }}>
                Test Get Simple Exams
            </button>
        </div>
    );
}
