import React, { useState, useEffect } from 'react';
import * as api from '../utils/api';
import '../styles/lists.css';

export default function ExamList() {
    const [exams, setExams] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchExams();
    }, []);

    const fetchExams = async () => {
        try {
            setLoading(true);
            setError('');

            let examList = [];
            try {
                // Try to get available exams for students
                const res = await api.getAvailableExams();
                examList = res.data || [];
            } catch (fullApiError) {
                console.log('Full API failed, trying simple API');
                // Fallback to simple API and filter active exams
                const res = await api.getSimpleExams();
                examList = (res.data || []).filter(exam => exam.isActive);
            }

            setExams(examList);
        } catch (err) {
            setError(err.message || 'Failed to load exams');
        } finally {
            setLoading(false);
        }
    };

    const handleStartExam = (exam) => {
        alert(`Starting exam: ${exam.title}\nDuration: ${exam.duration} minutes`);
        // Here you would navigate to the exam interface
        // navigate(`/student/exams/${exam.examId}`);
    };

    if (loading) return <div className="loading">Loading exams...</div>;
    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="dashboard-container">
            <div className="container">
                <div className="list-container">
                    <div className="list-header">
                        <h2 className="list-title">Available Exams</h2>
                        <span className="list-count">{exams.length} exams</span>
                    </div>

                    <div className="list-body">
                        {exams.length === 0 ? (
                            <div className="empty-state">
                                <div className="empty-icon">üìù</div>
                                <h3 className="empty-title">No Active Exams</h3>
                                <p className="empty-description">
                                    There are no active exams available at the moment. Please check back later.
                                </p>
                            </div>
                        ) : (
                            exams.map((exam) => (
                                <div key={exam.examId} className="list-item">
                                    <div className="item-header">
                                        <h3 className="item-title">{exam.title}</h3>
                                        <div className="item-meta">
                                            <span className="status-badge status-active">Active</span>
                                        </div>
                                    </div>

                                    <p className="item-description">{exam.description}</p>

                                    <div className="item-stats">
                                        <div className="stat-item">
                                            <span className="stat-icon">‚è±Ô∏è</span>
                                            <span>{exam.duration} minutes</span>
                                        </div>
                                        <div className="stat-item">
                                            <span className="stat-icon">üë®‚Äçüè´</span>
                                            <span>By {exam.createdBy || 'Teacher'}</span>
                                        </div>
                                    </div>

                                    <div className="item-actions">
                                        <button 
                                            className="action-btn-small btn-primary"
                                            onClick={() => handleStartExam(exam)}
                                        >
                                            üöÄ Start Exam
                                        </button>
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}
